import React from 'react';

// Sample component to display class-wise textbook links
function CourseContent() {
  // Assuming we have a simple array for class names
  // In a real app, this could come from props, state, or an API
  const classes = [
    { id: 1, name: 'Class 1', downloadLink: '/downloads/class1book.pdf' },
    { id: 2, name: 'Class 2', downloadLink: '/downloads/class2book.pdf' },
    { id: 3, name: 'Class 3', downloadLink: '/downloads/class3book.pdf' },
    // Add more classes as needed
  ];

  return (
    <div className="class-books-container">
      <ul>
        {classes.map((cls) => (
          <li key={cls.id}>
            {cls.name} <a href={cls.downloadLink} download>Download Book</a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CourseContent;
