import "./course.css";

export default function Courses({ increment, decrement }) {
  // const [courses, setCourses] = useState([]);

  // useEffect(() => {
  //   fetch('http://localhost:4000/courses')
  //     .then(response => {
  //       if (!response.ok) {
  //         throw new Error('Network response was not ok');
  //       }
  //       console.log("response.json() :"+response.json())
  //       return response.json();
  //     })
  //     .then(data => {
  //       setCourses(data); // Assuming 'data' is an array of course names
  //     })
  //     .catch(error => console.error('There was an error fetching the courses:', error));
  // }, []);

  const courses  = [ 
  'Full Stack Java',
  'Artificial Intelligence',
  'Machine Learning',
  'Web Designing',
  'Data Science']
  return (
    <>
      <div>
        <h1 id="start">Advance Courses We Offer:</h1>
        <ul id="last">
          {courses.map((course, index) => (
            <li key={index}>{course}
              <div className="btn-style">
                <button id="add" onClick={increment}>Add</button>
                <button id="remove" onClick={decrement}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}
