import React, { useState, useEffect } from "react";
import "./home.css"

export default function Home() {

  const images = [
    "/public/images/pic1.png",
    "/public/images/pic2.png",
    "/public/images/pic3.png",
    "/public/images/pic4.png",
  ];
   // State to keep track of the current image index
   const [currentImageIndex, setCurrentImageIndex] = useState(0);

   // Effect to change the image every 3 seconds (3000 milliseconds)
   useEffect(() => {
     const interval = setInterval(() => {
       setCurrentImageIndex((currentImageIndex + 1) % images.length);
     }, 3000);
     return () => clearInterval(interval);
   }, [currentImageIndex, images.length]);
  return (
    <>
    <div>
    <div className="home1">
      <p id="about-home">To help your child enhance the understanding of life based on the core concept of the most powerful tool in the world - “THE MEMORY POWER”.</p>
      <img src="/images/pic6.png" alt="" />
    </div>

    </div>

    </>
  );
}
