import React, { useState, useEffect } from "react";
import "./course.css";

export default function Courses({ increment, decrement }) {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Consider using async/await for better readability
    const fetchCourses = async () => {
      try {
        const response = await fetch('http://localhost:4000/courses');
        const data = await response.json();
        setCourses(data.courses);
      } catch (error) {
        console.error('There was an error fetching the courses:', error);
      }
    };

    fetchCourses();
  }, []);

  return (
    <div className="courses-container">
      <h1 className="courses-heading">Courses We Offer:</h1>
      <ul className="courses-list">
        {/* {courses.map((course, index) => (
          <li key={index} className="course-item">
            {course}
            <div className="buttons-container">
              <button className="add-btn" onClick={increment}>Add</button>
              <button className="remove-btn" onClick={decrement}>Remove</button>
            </div>
          </li>
        ))} */}
      </ul>
    </div>
  );
}
