import React from "react";
import "./course.css"
import  { useState, useEffect } from 'react';

export default function Courses() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch the courses data from your API
    fetch('http://localhost:4000/courses') // Adjust this URL to where your backend is hosted, e.g., http://localhost:3000/data
        .then(response => response.json())
        .then(data => {
            // Assuming your data structure looks like { "courses": [ "Course1", "Course2", ... ] }
            setCourses(data.courses); // Update the state with the fetched courses
        })
        .catch(error => console.error('There was an error fetching the courses:', error));
}, []); 
  return (
    <>
    <div>
      <h1 id="start">Courses We offer :</h1>
      <ul id="last">
      {courses.map((course, index) => (
                    <li key={index}>{course}</li> // Use course or index as a key as appropriate
                ))}
      </ul>
    </div>
    </>
  );
}
