import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Home from "./component/home/home";
import Header from './component/header/header';
import Footer from './component/footer/footer';
import AboutUs from './component/about/about';
import Courses from './component/courses/course';
import Login from './component/login/login';
import ShowCart from './component/cart/showCart';
import ContactUs from './component/contactUs/contactUs';
import React, { useState } from "react";

function App() {

  const [courseCount, setCourseCount] = useState(0);

  const increment = () => {
    setCourseCount(courseCount + 1);
  };

  const decrement = () => {
    if (courseCount > 0) {
      setCourseCount(courseCount - 1);
    }
  };

  return (
    <>
      <Router>
        <Header courseCount={courseCount} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/contact-us" element={<ContactUs />} />
          <Route path="/about-us" element={<AboutUs />} />
          <Route path="/courses" element={<Courses increment={increment} decrement={decrement} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/showcart" element={<ShowCart />} />
          <Route path="/showcart" element={<ShowDemo />} />
        </Routes>
        <Footer />
      </Router>
    </>
  );
}

export default App;
