import React, { createContext, useState,useEffect } from "react";
import "./course.css"
export default function Courses() {
  const [courses, setCourses] = useState("");

  useEffect(() => {
    // Fetch the courses data from your API
    fetch('http://localhost:4000/courses') 
        .then(response => response.json())
        .then(data => {
            setCourses(data.courses);
        })
        .catch(error => console.error('There was an error fetching the courses:', error));
}, []); 
  return (
    <>
    <div>
      <h1 id="start">Courses We offer :</h1>
      <ul id="last">
      {courses.map((course, index) => (
                    <li key={index}>{course}</li> // Use course or index as a key as appropriate
                ))}
      </ul>
    </div>
    </>
  );
}
