import React, { useState, useEffect } from "react";
import "./course.css";

export default function Courses({ increment, decrement }) {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Ensure the URL is correct and your server is running
    fetch('http://localhost:4000/courses')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        // Assuming the data structure matches our assumption
        // If not, you might need to adjust this line
        setCourses(data.courses);
      })
      .catch(error => console.error('There was an error fetching the courses:', error));
  }, []); // Empty dependency array means this effect runs once on mount


  // const courses  = [ 
  // 'Full Stack Java',
  // 'Artificial Intelligence',
  // 'Machine Learning',
  // 'Web Designing',
  // 'Data Science'];

     return (
    <div className="courses-container">
      <h1 className="courses-heading">Advance Courses We Offer:</h1>
      <ul className="courses-list">
        {courses.map((course, index) => (
          <li key={index} className="course-item">
            <span>{course}</span> 
            <div className="buttons-container">
              <button className="add-btn" onClick={() => increment(course)} >Add</button>
              <button className="remove-btn" onClick={() => decrement(course)} >Remove</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
