import React, {useState,useEffect } from "react";
import "./course.css"
export default function Courses({increment,decrement}) {
  const [courses, setCourses] = useState([]);
  useEffect(() => {
    fetch('http://localhost:4000/courses') 
        .then(response => response.json())
        .then(data => {
            setCourses(data.courses);
        })
        .catch(error => console.error('There was an error fetching the courses:', error));
}, []); 
  return (
    <>
    <div>
      <h1 id="start">Courses We offer :</h1>
      <ul id="last">
          {courses.map((course, index) => (
            <li key={index}>{course}
            <div className="btn-style">
            <button id="add" onClick={increment}>add</button>
            <button id="remove" onClick={decrement}>remove</button></div>
           </li>
          ))}
      </ul>
    </div>
    </>
  );
}
