import React from "react";
import { useState } from 'react';
import "./login.css"






function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLogin, setIsLogin] = useState(true); // True for login, False for signup

  const handleSubmit = (event) => {
    event.preventDefault();

    // Simulate login or signup (replace with your actual authentication logic)
    if (isLogin) {
      console.log('Login attempt with email:', email, 'password:', password);
    } else {
      console.log('Signup attempt with email:', email, 'password:', password);
    }

    // Clear form fields (optional)
    setEmail('');
    setPassword('');
  };

  const toggleLoginSignup = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="login-container">
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
      </form>
      {!isLogin && (
        <p>
          Already have an account? <a onClick={toggleLoginSignup}>Login Here</a>
        </p>
      )}
      {isLogin && (
        <p>
          Don't have an account? <a onClick={toggleLoginSignup}>Sign Up</a>
        </p>
      )}
    </div>
  );
}

export default Login;
