import React, { useState } from 'react';
import "./login.css";

function Login() {
  const [isLoginMode, setIsLoginMode] = useState(true); // True for login, False for signup
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const [popupType, setPopupType] = useState('success'); // 'success' or 'error'


  const toggleMode = () => {
    setIsLoginMode(!isLoginMode);
  };
  const handleSubmit = (event) => {
    event.preventDefault();

    const email = document.getElementById('email').value;
    const password = document.getElementById('password').value;

    if (email === 'user@example.com' && password === 'password') {
      setPopupMessage('Login Successfully!');
      setPopupType('success');
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
    } else {
      setPopupMessage('Login Failed!');
      setPopupType('error');
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
    }
  };


  return (
    <div className="login-container">
      <h1>{isLoginMode ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={handleSubmit}>
        {isLoginMode ? (
          <>
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" required />
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" required />
          </>
        ) : (
          <>
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" required />
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" required />
          </>
        )}
        <button type="submit">{isLoginMode ? 'Login' : 'Sign Up'}</button>
      </form>
      {showPopup && (
  <div
    className="popup"
    style={{
      backgroundColor: popupType === 'success' ? '#4CAF50' : '#f44336', // Green for success, red for error
      color: '#fff', // White text color
    }}
  >
    {popupMessage}
  </div>
)}
      <p>
        {isLoginMode ? (
          "Don't have an account? "
        ) : (
          "Already have an account? "
        )}
        <a href="#" onClick={toggleMode}>
          {isLoginMode ? 'Sign Up Here' : 'Login Here'}
        </a>
      </p>
    </div>
  );
}

export default Login;
