import React from "react";
import { Link } from "react-router-dom"; // Import Link
import "./header.css"
import  { useContext} from 'react';
import CourseContext from './component/courses/courseContext';


export default function Header() {

  const { count } = useContext(CourseContext);
  return (
    <>
    <nav>
      <img src="./images/pic13.png" alt=""  className="image"/>
      <ul className="nav-menu">
      <li><Link to="/">Home</Link></li>
      <li><Link to="/about-us">About Us</Link></li>
      <li><Link to="/courses">Course</Link></li>
      <li><Link to="/contact-us">Contact Us</Link></li>
      <li><button>Join Class</button></li>
      <li><button>Book A Free Trial</button></li>
      <li>Total course selected :{count}</li>
      </ul>
 
      </nav>
    </>
  );
}
